DECLARE ret_val asset_class;
BEGIN
	UPDATE asset_class 
	SET asset_account_id = in_asset_account_id,
		dep_account_id = in_dep_account_id,
		method = in_method,
		label = in_label
	WHERE id = in_id;

	IF FOUND THEN
		SELECT * INTO ret_val FROM asset_class where id = in_id;
		RETURN ret_val;
	END IF;

	INSERT INTO asset_class (asset_account_id, dep_account_id, method,
		label)
	VALUES (in_asset_account_id, in_dep_account_id, in_method, 
		in_label);

	SELECT * INTO ret_val FROM asset_class 
	WHERE id = currval('asset_class_id_seq');

	RETURN ret_val;
END;

Function Properties
Language Return Type
SQL boolean
Performs straight line depreciation, selecting depreciation amounts, etc. into a report for further review and approval. Usable life is in months, and depreciation is an equal amount every month.
INSERT INTO asset_report_line (asset_id, report_id, amount, department_id, warehouse_id) SELECT ai.id, $3, asset_dep__straight_line_base( ai.usable_life, ai.usable_life --months - months_passed(coalesce(start_depreciation, purchase_date), coalesce(max(report_date), start_depreciation, purchase_date)), months_passed(coalesce(max(report_date), start_depreciation, purchase_date), $2), purchase_value - salvage_value, coalesce(sum(l.amount), 0)), ai.department_id, ai.location_id FROM asset_item ai LEFT JOIN asset_report_line l ON (l.asset_id = ai.id) LEFT JOIN asset_report r ON (l.report_id = r.id) WHERE ai.id = ANY($1) GROUP BY ai.id, ai.start_depreciation, ai.purchase_date, ai.purchase_value, ai.salvage_value, ai.department_id, ai.location_id, ai.usable_life;
UPDATE asset_report SET report_class = 1 WHERE id = $3;
select true;

