PLPGSQL integer
Reverses a payment. All fields are mandatory except batch_id and voucher_id because they determine the identity of the payment to be reversed.
DECLARE pay_row record; t_voucher_id int; t_voucher_inserted bool; BEGIN IF in_batch_id IS NOT NULL THEN t_voucher_id := nextval('voucher_id_seq'); t_voucher_inserted := FALSE; END IF; FOR pay_row IN SELECT a.*, c.ar_ap_account_id FROM acc_trans a JOIN (select id, entity_credit_account FROM ar WHERE in_account_class = 2 UNION SELECT id, entity_credit_account FROM ap WHERE in_account_class = 1 ) arap ON (a.trans_id = arap.id) JOIN entity_credit_account c ON (arap.entity_credit_account = c.id) JOIN chart ch ON (a.chart_id = ch.id) WHERE coalesce(source, '') = coalesce(in_source, '') AND transdate = in_date_paid AND in_credit_id = c.id AND in_cash_accno = ch.accno and coalesce (in_voucher_id, 0) = coalesce(voucher_id, 0) LOOP IF in_batch_id IS NOT NULL AND t_voucher_inserted IS NOT TRUE THEN INSERT INTO voucher (id, trans_id, batch_id, batch_class) VALUES (t_voucher_id, pay_row.trans_id, in_batch_id, CASE WHEN in_account_class = 1 THEN 4 WHEN in_account_class = 2 THEN 7 END);
t_voucher_inserted := TRUE; END IF;
INSERT INTO acc_trans (trans_id, chart_id, amount, transdate, source, memo, approved, voucher_id) VALUES (pay_row.trans_id, pay_row.chart_id, pay_row.amount * -1, in_date_reversed, in_source, 'Reversing ' || COALESCE(in_source, ''), case when in_batch_id is not null then false else true end, t_voucher_id); INSERT INTO acc_trans (trans_id, chart_id, amount, transdate, source, memo, approved, voucher_id) VALUES (pay_row.trans_id, pay_row.ar_ap_account_id, pay_row.amount, in_date_reversed, in_source, 'Reversing ' || COALESCE(in_source, ''), case when in_batch_id is not null then false else true end, t_voucher_id); IF in_account_class = 1 THEN UPDATE ap SET paid = amount - (SELECT sum(a.amount) FROM acc_trans a JOIN chart c ON (a.chart_id = c.id) WHERE c.link = 'AP' AND trans_id = pay_row.trans_id ), force_closed = false WHERE id = pay_row.trans_id; ELSIF in_account_class = 2 THEN update ar SET paid = amount - (SELECT sum(a.amount) FROM acc_trans a JOIN chart c ON (a.chart_id = c.id) WHERE c.link = 'AR' AND trans_id = pay_row.trans_id ) * -1, force_closed = false WHERE id = pay_row.trans_id; ELSE RAISE EXCEPTION 'Unknown account class for payments %', in_account_class; END IF; END LOOP; RETURN 1; END;

para.programlisting:
DECLARE 
	out_count int;
	t_voucher_id int;
	t_trans_id int;
	t_amount numeric;
        t_ar_ap_id int;
	t_cash_id int;
        t_currs text[];
        t_exchangerate numeric;
BEGIN
	IF in_batch_id IS NULL THEN
		-- t_voucher_id := NULL;
		RAISE EXCEPTION 'Bulk Post Must be from Batch!';
	ELSE
		INSERT INTO voucher (batch_id, batch_class, trans_id)
		values (in_batch_id,
                (SELECT batch_class_id FROM batch WHERE id = in_batch_id),
                in_transactions[1][1]);

		t_voucher_id := currval('voucher_id_seq');
	END IF;

	SELECT string_to_array(value, ':') into t_currs 
          from defaults 
         where setting_key = 'curr';

        IF (in_curr IS NULL OR in_curr = t_currs[0]) THEN
                t_exchangerate := 1;
        ELSE 
                t_exchangerate := in_exchangerate;
        END IF;

	CREATE TEMPORARY TABLE bulk_payments_in (id int, amount numeric);

	select id into t_ar_ap_id from chart where accno = in_ar_ap_accno;
	select id into t_cash_id from chart where accno = in_cash_accno;

	FOR out_count IN 
			array_lower(in_transactions, 1) ..
			array_upper(in_transactions, 1)
	LOOP
		EXECUTE $E$
			INSERT INTO bulk_payments_in(id, amount)
			VALUES ($E$ || quote_literal(in_transactions[out_count][1])
				|| $E$, $E$ ||
				quote_literal(in_transactions[out_count][2])
				|| $E$)$E$;
	END LOOP;
	EXECUTE $E$ 
		INSERT INTO acc_trans 
			(trans_id, chart_id, amount, approved, voucher_id, transdate, 
			source)
		SELECT id, 
		case when $E$ || quote_literal(in_account_class) || $E$ = 1
			THEN $E$ || t_cash_id || $E$
			WHEN $E$ || quote_literal(in_account_class) || $E$ = 2 
			THEN $E$ || t_ar_ap_id || $E$
			ELSE -1 END, 
		amount * $E$|| quote_literal(t_exchangerate) || $E$,
		CASE 
			WHEN $E$|| t_voucher_id || $E$ IS NULL THEN true
			ELSE false END,
		$E$ || t_voucher_id || $E$, $E$|| quote_literal(in_payment_date) 
		||$E$ , $E$ ||COALESCE(quote_literal(in_source), 'NULL') || 
		$E$ 
		FROM bulk_payments_in  where amount <> 0 $E$;

	EXECUTE $E$ 
		INSERT INTO acc_trans 
			(trans_id, chart_id, amount, approved, voucher_id, transdate, 
			source)
		SELECT id, 
		case when $E$ || quote_literal(in_account_class) || $E$ = 1 
			THEN $E$ || t_ar_ap_id || $E$
			WHEN $E$ || quote_literal(in_account_class) || $E$ = 2 
			THEN $E$ || t_cash_id || $E$
			ELSE -1 END, 
		amount * -1 * $E$|| quote_literal(t_exchangerate) || $E$,
		CASE 
			WHEN $E$|| t_voucher_id || $E$ IS NULL THEN true
			ELSE false END,
		$E$ || t_voucher_id || $E$, $E$|| quote_literal(in_payment_date) 
		||$E$ , $E$ ||COALESCE(quote_literal(in_source), 'null') 
		||$E$ 
		FROM bulk_payments_in where amount <> 0 $E$;

        IF in_account_class = 1 THEN
        	EXECUTE $E$
	        	UPDATE ap 
		        set paid = paid + (select amount from bulk_payments_in b
		         	where b.id = ap.id),
                            datepaid = $E$ || quote_literal(in_payment_date) || $E$
		         where id in (select id from bulk_payments_in) $E$;
        ELSE
        	EXECUTE $E$
	        	UPDATE ar 
		        set paid = paid + (select amount from bulk_payments_in b 
		         	where b.id = ar.id),
                            datepaid = $E$ || quote_literal(in_payment_date) || $E$
		         where id in (select id from bulk_payments_in) $E$;
        END IF;
	EXECUTE $E$ DROP TABLE bulk_payments_in $E$;
	perform unlock_all();
	return out_count;
END;

