DECLARE out_payment payment_header_item; BEGIN FOR out_payment IN SELECT p.id as payment_id, p.reference as payment_reference, p.payment_date, c.legal_name as legal_name, am.amount as amount, em.first_name, em.last_name, p.currency, p.notes FROM payment p JOIN entity_employee ent_em ON (ent_em.entity_id = p.employee_id) JOIN person em ON (ent_em.entity_id = em.entity_id) JOIN entity_credit_account eca ON (eca.id = p.entity_credit_id) JOIN company c ON (c.entity_id = eca.entity_id) JOIN payment_links pl ON (p.id = pl.payment_id) LEFT JOIN ( SELECT sum(a.amount) as amount FROM acc_trans a JOIN account acc ON (a.chart_id = acc.id) JOIN account_link al ON (acc.id =al.account_id) JOIN payment_links pl ON (pl.entry_id=a.entry_id) WHERE al.description in ('AP_paid', 'AP_discount', 'AR_paid', 'AR_discount') and ((in_account_class = 1 AND al.description like 'AP%') or (in_account_class = 2 AND al.description like 'AR%')) ) am ON (true) WHERE p.id = in_payment_id LOOP RETURN NEXT out_payment; END LOOP;
END;

