section.para:
Function Properties
Language Return Type
PLPGSQL integer
Posts a payment. in_op_* arrays are cross-indexed with eachother. Other arrays are cross-indexed with eachother.
This API will probably change in 1.4 as we start looking at using more custom complex types and arrays of those (requires Pg 8.4 or higher).
DECLARE var_payment_id int; DECLARE var_gl_id int; DECLARE var_entry record; DECLARE var_entry_id int[]; DECLARE out_count int; DECLARE coa_id record; DECLARE var_employee int; DECLARE var_account_id int; DECLARE default_currency char(3); DECLARE current_exchangerate numeric; DECLARE old_exchangerate numeric; DECLARE tmp_amount numeric; BEGIN
SELECT * INTO default_currency FROM defaults_get_defaultcurrency(); SELECT * INTO current_exchangerate FROM currency_get_exchangerate(in_curr, in_datepaid, in_account_class);
SELECT INTO var_employee p.id FROM users u JOIN person p ON (u.entity_id=p.entity_id) WHERE username = SESSION_USER LIMIT 1; -- -- WE HAVE TO INSERT THE PAYMENT, USING THE GL INFORMATION -- THE ID IS GENERATED BY payment_id_seq -- INSERT INTO payment (reference, payment_class, payment_date, employee_id, currency, notes, department_id, entity_credit_id) VALUES ((CASE WHEN in_account_class = 1 THEN setting_increment('rcptnumber') -- I FOUND THIS ON sql/modules/Settings.sql ELSE -- and it is very usefull setting_increment('paynumber') END), in_account_class, in_datepaid, var_employee, in_curr, in_notes, in_department_id, in_entity_credit_id); SELECT currval('payment_id_seq') INTO var_payment_id; -- WE'LL NEED THIS VALUE TO USE payment_link table -- WE'LL NEED THIS VALUE TO JOIN WITH PAYMENT -- NOW COMES THE HEAVY PART, STORING ALL THE POSSIBLE TRANSACTIONS... -- -- FIRST WE SHOULD INSERT THE CASH ACCOUNTS -- -- WE SHOULD HAVE THE DATA STORED AS (ACCNO, AMOUNT), SO IF (array_upper(in_cash_account_id, 1) > 0) THEN FOR out_count IN array_lower(in_cash_account_id, 1) .. array_upper(in_cash_account_id, 1) LOOP INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source, memo) VALUES (in_cash_account_id[out_count], CASE WHEN in_account_class = 1 THEN in_amount[out_count]*current_exchangerate ELSE (in_amount[out_count]*current_exchangerate)* - 1 END, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count], in_memo[out_count]); INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 1); IF (in_ovp_payment_id IS NOT NULL AND in_ovp_payment_id[out_count] IS NOT NULL) THEN INSERT INTO payment_links VALUES (in_ovp_payment_id[out_count], currval('acc_trans_entry_id_seq'), 0); END IF;
END LOOP; -- NOW LETS HANDLE THE AR/AP ACCOUNTS -- WE RECEIVED THE TRANSACTIONS_ID AND WE CAN OBTAIN THE ACCOUNT FROM THERE FOR out_count IN array_lower(in_transaction_id, 1) .. array_upper(in_transaction_id, 1) LOOP SELECT INTO var_account_id chart_id FROM acc_trans as ac JOIN chart as c ON (c.id = ac.chart_id) WHERE trans_id = in_transaction_id[out_count] AND ( c.link = 'AP' OR c.link = 'AR' ); -- We need to know the exchangerate of this transaction IF (current_exchangerate = 1 ) THEN old_exchangerate := 1; ELSIF (in_account_class = 1) THEN SELECT buy INTO old_exchangerate FROM exchangerate e JOIN ap a on (a.transdate = e.transdate ) WHERE a.id = in_transaction_id[out_count]; ELSE SELECT sell INTO old_exchangerate FROM exchangerate e JOIN ar a on (a.transdate = e.transdate ) WHERE a.id = in_transaction_id[out_count]; END IF; -- Now we post the AP/AR transaction INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source, memo) VALUES (var_account_id, CASE WHEN in_account_class = 1 THEN
(in_amount[out_count]*old_exchangerate) * -1 ELSE in_amount[out_count]*old_exchangerate END, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count], in_memo[out_count]); -- Lets set the gain/loss, if tmp_amount equals zero then we dont need to post -- any transaction tmp_amount := in_amount[out_count]*current_exchangerate - in_amount[out_count]*old_exchangerate; IF (tmp_amount < 0) THEN IF (in_account_class = 1) THEN INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source) VALUES (CAST((select value from defaults where setting_key like 'fxloss_accno_id') AS INT), tmp_amount, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count]); ELSE INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source) VALUES (CAST((select value from defaults where setting_key like 'fxgain_accno_id') AS INT), tmp_amount, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count]); END IF; ELSIF (tmp_amount > 0) THEN IF (in_account_class = 1) THEN INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source) VALUES (CAST((select value from defaults where setting_key like 'fxgain_accno_id') AS INT), tmp_amount, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count]); ELSE INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source) VALUES (CAST((select value from defaults where setting_key like 'fxloss_accno_id') AS INT), tmp_amount, in_transaction_id[out_count], in_datepaid, coalesce(in_approved, true), in_source[out_count]); END IF; END IF; -- Now we set the links INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 1); END LOOP; END IF; -- END IF -- -- WE NEED TO HANDLE THE OVERPAYMENTS NOW -- -- -- FIRST WE HAVE TO MAKE THE GL TO HOLD THE OVERPAYMENT TRANSACTIONS -- THE ID IS GENERATED BY gl_id_seq --
IF (array_upper(in_op_cash_account_id, 1) > 0) THEN INSERT INTO gl (reference, description, transdate, person_id, notes, approved, department_id) VALUES (setting_increment('glnumber'), in_gl_description, in_datepaid, var_employee, in_notes, in_approved, in_department_id); SELECT currval('id') INTO var_gl_id; -- -- WE NEED TO SET THE GL_ID FIELD ON PAYMENT'S TABLE -- UPDATE payment SET gl_id = var_gl_id WHERE id = var_payment_id; -- NOW COMES THE HEAVY PART, STORING ALL THE POSSIBLE TRANSACTIONS... -- -- FIRST WE SHOULD INSERT THE OVERPAYMENT CASH ACCOUNTS -- FOR out_count IN array_lower(in_op_cash_account_id, 1) .. array_upper(in_op_cash_account_id, 1) LOOP INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source, memo) VALUES (in_op_cash_account_id[out_count], CASE WHEN in_account_class = 1 THEN in_op_amount[out_count] ELSE in_op_amount[out_count] * - 1 END, var_gl_id, in_datepaid, coalesce(in_approved, true), in_op_source[out_count], in_op_memo[out_count]); INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 2);
END LOOP; -- NOW LETS HANDLE THE OVERPAYMENT ACCOUNTS FOR out_count IN array_lower(in_op_account_id, 1) .. array_upper(in_op_account_id, 1) LOOP INSERT INTO acc_trans (chart_id, amount, trans_id, transdate, approved, source, memo) VALUES (in_op_account_id[out_count], CASE WHEN in_account_class = 1 THEN in_op_amount[out_count] * -1 ELSE in_op_amount[out_count] END, var_gl_id, in_datepaid, coalesce(in_approved, true), in_op_source[out_count], in_op_memo[out_count]); INSERT INTO payment_links VALUES (var_payment_id, currval('acc_trans_entry_id_seq'), 2); END LOOP; END IF; return var_payment_id; END;


