Saves saves contact info. Returns 1 if a row was inserted, 0 if it was updated.
DECLARE out_id int; v_orig person_to_contact; BEGIN
SELECT cc.* into v_orig FROM person_to_contact cc, person p WHERE p.entity_id = in_entity_id and cc.contact_class_id = in_contact_class AND cc.contact = in_contact_orig AND cc.person_id = p.id;
IF NOT FOUND THEN
-- create INSERT INTO person_to_contact(person_id, contact_class_id, contact) VALUES ( (SELECT id FROM person WHERE entity_id = in_entity_id), in_contact_class, in_contact_new ); return 1; ELSE -- edit. UPDATE person_to_contact SET contact = in_contact_new WHERE contact = in_contact_orig AND person_id = v_orig.person_id AND contact_class = in_contact_class; return 0; END IF;
END;

